// Get original article data (from previous node)
const originalArticle = items[0].json;

// Parse AI JSON response
let aiAnalysis;
try {
  // Extract JSON from markdown code blocks if present
  const jsonMatch = aiResponse.match(/```json\n([\s\S]*?)\n```/) || 
                    aiResponse.match(/\{[\s\S]*\}/);
  aiAnalysis = JSON.parse(jsonMatch ? jsonMatch[1] || jsonMatch[0] : aiResponse);
} catch (error) {
  // Fallback if parsing fails
  aiAnalysis = {
    relevanceScore: 95,
    suggestedCourseTitle: originalArticle.title,
    suggestedDescription: originalArticle.description,
    keywords: originalArticle.keywords,
    estimatedDurationMinutes: 120,
    estimatedGenerationCostUsd: 2.0,
    keyTopics: originalArticle.keywords
  };
}

return {
  source: 'azure-docs',
  sourceId: originalArticle.id,
  sourceUrl: originalArticle.url,
  title: originalArticle.title,
  description: originalArticle.description,
  keywords: originalArticle.keywords,
  trendScore: originalArticle.score,
  aiCourseProposal: {
    relevanceScore: aiAnalysis.relevanceScore,
    suggestedCourseTitle: aiAnalysis.suggestedCourseTitle,
    suggestedDescription: aiAnalysis.suggestedDescription,
    keywords: aiAnalysis.keywords,
    keyTopics: aiAnalysis.keyTopics || aiAnalysis.keywords,
    estimatedDurationMinutes: aiAnalysis.estimatedDurationMinutes,
    hackernewsData: {
      author: originalArticle.author,
      time: originalArticle.time,
      score: originalArticle.score
    }
  },
  estimatedDurationMinutes: aiAnalysis.estimatedDurationMinutes,
  estimatedGenerationCostUsd: aiAnalysis.estimatedGenerationCostUsd,
  estimatedEngagementScore: Math.round(aiAnalysis.relevanceScore)
};
